$(document).ready(function() {
    $('a[href^="#"]').on("click", function(e) {
        var t = $($(this).attr("href"));
        t.length && (e.preventDefault(), $("html, body").animate({
            scrollTop: t.offset().top - 10
        }, 1e3))
    }), $(".acc h3").click(function() {
        $(this).hasClass("selected") ? $(this).addClass("minus") : $(this).removeClass("minus")
    }), $(window).on("resize", function() {
        $(window).width() < 768 ? $(".about-sec div.row").removeClass("vertical-align") : $(".about-sec div.row").addClass("vertical-align")
    }), $(window).width() < 768 && $("#about div.vertical-align").removeClass("vertical-align")
}), window.requestAnimationFrame = window.requestAnimationFrame || window.mozRequestAnimationFrame || window.webkitRequestAnimationFrame || window.msRequestAnimationFrame || function(e) {
    setTimeout(e, 1e3 / 60)
};